AOS.init({once:!1,duration:1500,offset:180});const body=document.querySelector("body");function accordion(c){const o=document.querySelectorAll(c+" [data-accordion-title]");o.forEach(function(c){c.addEventListener("click",function(){c.classList.contains("active")?c.classList.remove("active"):(o.forEach(function(c){c.classList.remove("active")}),c.classList.add("active"))})})}accordion(".accordionFaq");